import { useCallback, useState } from 'react'
import './App.css';

function App() {

  const [length, setLength] = useState(8)
  const [number, setNumber] = useState(false)
  const [character, setCharacter] = useState(false)
  const [password, setPassword] = useState(false)

  const passwordGenerator = useCallback(() => {
    let pass = ""
    let str = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz"
    if (number) str += "0123456789"
    if (character) str += "!@#$%^&*()-_=+[]{}|;:"

    // for (let i = 1; i <= array.length; i++) {
    //   let ind = Math.floor(Math.random() * str.length + 1);
    //   pass = str.charAt(ind)
    // }
    setPassword(pass)

  }, [setLength, setNumber, setCharacter, setPassword])

  return (
    <>
      {/* <div className='px-10'>Hello</div> */}
      <div className="w-full max-w-md mx-auto shadow-md rounded-lg px-4 py-3 my-8 bg-gray-800 text-orange-500">
        <h1 className='text-white text-center my-3'>Password generator</h1>
        {/* <div className='w-full max-w-md mx-auto shadow-md rounded-lg px-4 my-8 text-orange-500 br-gray'>Test</div> */}
        <div className="flex shadow rounded-lg overflow-hidden mb-4">
          <input
            type="text"
            value={password}
            className='outline-none w-full py-full py-1 px-3'
            placeholder='password'
            readOnly
          />
          <button
            // onClick={copyPasswordToClipboard}
            className='outline-none bg-blue-700 text-white px-3 py-0.5 shrink-0'
          >copy</button>
          <div className='flex text-sm gap-x-2'>
            <div className='flex items-center gap-x-1'>
              <input
                type="range"
                min={6}
                max={100}
                value={length}
                className='cursor-pointer'
                onChange={(e) => { setLength(e.target.value) }}
              />
              <label>Length: {length}</label>
            </div>
          </div>
        </div>
      </div>
    </>

  );
}

export default App;
